//CurtainBlock.js
function CurtainBlock(persistent,transparency,redir){$curtain=$('.CurtainBlock');$alert=$('.CurtainBlockAlert');$confirm=$('.CurtainBlockConfirm');$deny=$('.CurtainBlockDeny');complete=0;if(persistent){if(localStorage.getItem("curtainConfirm")){$curtain.hide();complete=1;};};if(complete===0){$confirm.show();$deny.show();if(transparency){$curtain.css('background-color','rgba(0,0,0,0.85');}$confirm.click(function(){$curtain.fadeOut(750);$alert.fadeOut(350);if(persistent){localStorage.setItem("curtainConfirm",true);};});$deny.click(function(){window.location.replace(redir);});}}