// CurtainBlock.js
CurtainBlock = {init: false,options: {persistent: false, redir: 'about:blank', transparency: false}};$(document).ready(function(){$curtain=$('.CurtainBlock');$alert=$('.CurtainBlockAlert');$confirm=$('.CurtainBlockConfirm');$deny=$('.CurtainBlockDeny');complete=0;if(CurtainBlock.init){if(CurtainBlock.options.persistent){if(localStorage.getItem("curtainConfirm")){$curtain.hide();complete=1;};};if(complete===0){$confirm.show();$deny.show();$alert.fadeIn(500);if(CurtainBlock.options.transparency){$curtain.css('background-color','rgba(0,0,0,0.85')}$confirm.click(function(){$curtain.fadeOut(750);$alert.fadeOut(350);if(CurtainBlock.options.persistent){localStorage.setItem("curtainConfirm",true);};});$deny.click(function(){if(CurtainBlock.options.redir.length===0){window.location.replace("about:blank")}else{window.location.replace(CurtainBlock.options.redir)}});}}else{$curtain.hide();console.log("The CurtainBlock.js code is here, but you didn't start it!~ Set CurtainBlock.init to true in your HTML to start it!");}});